sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    // Log the current wine_reference, quantity, and status
    gs.info('Current wine_reference (sys_id): ' + current.wine_reference + 
             ', Quantity: ' + current.quantity + 
             ', Status: ' + current.status);

    // Check if the quantity is greater than zero
    if (current.quantity > 0) {
        var wineReference = current.wine_reference; // This should be the sys_id of the wine
        var gr = new GlideRecord('x_1394653_wine_inv_wine_inventory_tracking');

        // Query for existing records with the same wine_reference
        gr.addQuery('wine_reference', wineReference);
        gr.query();

        // If a matching record exists, update the quantity based on status
        if (gr.next()) {
            gs.info('Found existing wine record: ' + wineReference);

            if (current.status === 'available') {
                // Update quantity for available status
                gr.quantity += current.quantity; // Always add to existing quantity
                gs.info('Updated quantity for available wine: ' + wineReference + ' to ' + gr.quantity);
            } else if (current.status === 'consumed') {
                // Log and do not change quantity for consumed
                gs.info('Wine is consumed, not updating quantity for: ' + wineReference);
                // Optionally: you could reset or log something here
            } else if (current.status === 'reserved') {
                // Log for reserved wines
                gs.info('Wine is reserved, no quantity update for: ' + wineReference);
            } else if (current.status === 'out_of_stock') {
                // Log for out of stock
                gs.info('Wine is out of stock, no quantity update for: ' + wineReference);
            }

            gr.update(); // Save the updated record
            current.setAbortAction(true); // Prevent inserting a new record
        } else {
            gs.info('No existing wine found, allowing new record to be inserted.');
            // Allow the insert if no existing record is found
            // Ensure all mandatory fields are set before allowing the insert
            if (!current.wine_reference || !current.quantity) {
                gs.error('Cannot insert new record; mandatory fields are missing.');
                current.setAbortAction(true); // Prevent insert
            }
        }
    } else {
        gs.info('Quantity is not greater than 0, no action taken.');
        current.setAbortAction(true); // Prevent insert if quantity is not valid
    }

})(current, previous);
